package cashflowcalculations.model;public class CashFlow {	private String category;	private double amount;	private ExpenditureDetails expenditureDetails;	private RecommendationDetails recommendationDetails;	public RecommendationDetails getRecommendationDetails() {		return recommendationDetails;	}	public void setRecommendationDetails(RecommendationDetails recommendationDetails) {		this.recommendationDetails = recommendationDetails;	}	public ExpenditureDetails getExpenditureDetails() {		return expenditureDetails;	}	public void setExpenditureDetails(ExpenditureDetails expenditureDetails) {		this.expenditureDetails = expenditureDetails;	}	public String getCategory() {		return category;	}	public CashFlow(String category, double amount) {		super();		this.category = category;		this.amount = amount;	}	public CashFlow() {		super();	}	public void setCategory(String category) {		this.category = category;	}	public double getAmount() {		return amount;	}	public void setAmount(double amount) {		this.amount = amount;	}}