package cashflowcalculations.test;import java.util.HashMap;import java.util.Map;import org.junit.Assert;import org.junit.jupiter.api.Test;import cashflowcalculations.util.CommonUtil;public class CommonUtilTest {	@Test	public void findMaxOccurrenceInMapWithHighestValueTest() {		String recommendedCategory = CommonUtil.findMaxOccurrenceInMapWithHighestValue(createRecommendationMap());		Assert.assertEquals("Entertainment", recommendedCategory);	}	@Test	public void checkIfMonthExistsTest() {		boolean valid = CommonUtil.checkIfMonthExists("January", createMonthlyCashFlowMap());		Assert.assertEquals(true, valid);	}	private Map<String, Double> createRecommendationMap() {		Map<String, Double> recommendationMap = new HashMap<>();		recommendationMap.put("Credit card", 1000.00);		recommendationMap.put("Entertainment", 1500.00);		recommendationMap.put("Food", 100.00);		return recommendationMap;	}	private Map<String, Double> createMonthlyCashFlowMap() {		Map<String, Double> monthlyCashMap = new HashMap<>();		monthlyCashMap.put("January", 100.0);		monthlyCashMap.put("February", 1000.0);		monthlyCashMap.put("June", 1200.0);		monthlyCashMap.put("December", 1500.0);		return monthlyCashMap;	}}