package cashflowcalculations.test;import java.util.HashMap;import java.util.Map;import org.junit.Assert;import org.junit.Test;import cashflowcalculations.model.RecommendationDetails;import cashflowcalculations.serviceimpl.ExpenditureAccumulationImpl;public class ExpenditureAccumulationImplTest {	ExpenditureAccumulationImpl expenditureAccumulationImpl = new ExpenditureAccumulationImpl();	@Test	public void getExpenditureAmountTest() {		double expenditureAmount = expenditureAccumulationImpl.getExpenditureAmount(getExpenditureMap());		Assert.assertEquals(3700.0, expenditureAmount, 0.00);	}	@Test	public void getSuggestionsForImprovementTest() {		RecommendationDetails recommendationDetails = expenditureAccumulationImpl				.getSuggestionsForImprovement(getExpenditureMap());		Assert.assertEquals(recommendationDetails, recommendationDetails);	}	private Map<String, Double> getExpenditureMap() {		Map<String, Double> expenditureMap = new HashMap<String, Double>();		expenditureMap.put("Credit Card", 1000.00);		expenditureMap.put("Entertainment", 1200.00);		expenditureMap.put("Food", 1500.00);		return expenditureMap;	}	private RecommendationDetails getRecomendationDetailsObject() {		RecommendationDetails recommendationDetails = new RecommendationDetails();		recommendationDetails.setAmount(1500.00);		recommendationDetails.setCategory("Food");		return recommendationDetails;	}}