package cashflowcalculations.test;import java.util.HashMap;import java.util.Map;import org.junit.Assert;import org.junit.Test;import cashflowcalculations.serviceimpl.IncomeAccumulationImpl;public class IncomeAccumulationImplTest {	IncomeAccumulationImpl incomeAccumulationImpl = new IncomeAccumulationImpl();	@Test	public void getExpenditureAmountTest() {		double incomeAmount = incomeAccumulationImpl.getIncomeAmount(getIncomeMap());		Assert.assertEquals(14200.00, incomeAmount, 0.00);	}	private Map<String, Double> getIncomeMap() {		Map<String, Double> expenditureMap = new HashMap<String, Double>();		expenditureMap.put("Salary", 10000.00);		expenditureMap.put("Rent", 2200.00);		expenditureMap.put("Stocks", 2000.00);		return expenditureMap;	}}