package cashflowcalculations.util;import java.util.Map;public class CommonUtil {	public static String findMaxOccurrenceInMapWithHighestValue(Map<String, Double> recommendationMap) {		double maxAmount = 0;		String category = null;		for (Map.Entry<String, Double> entry : recommendationMap.entrySet()) {			if (entry.getValue() > maxAmount) {				category = entry.getKey();				maxAmount = entry.getValue();			}		}		return category;	}	public static boolean checkIfMonthExists(String month, Map<String, Double> monthlyCashFlowMap) {		for (Map.Entry<String, Double> map : monthlyCashFlowMap.entrySet()) {			if (map.getKey().equalsIgnoreCase(month))				return true;		}		return false;	}}